mapper.select:
SELECT * FROM app_order

    status=#{status}

        AND ap_ord.create_time >= #{startTime}


       AND ap_ord.create_time <= #{endTime}

SELECT * FROM app_order ap_ord

    status=#{status}

        AND ap_ord.order_num in(SELECT order_num FROM app_order_detail where mobile = #{KeyWord})


        AND ap_ord.create_time >= #{startTime}


        AND ap_ord.create_time <= #{endTime}

SELECT * FROM app_order ap_ord

    status=#{status}

        AND ap_ord.product_id in(SELECT id FROM app_product ap_pro where ap_pro.name like #{KeyWord})


        AND ap_ord.create_time >= #{startTime}


        AND ap_ord.create_time <= #{endTime}
