mapper.update:
UPDATE app_express_address a
SET a.deleted = 1
WHERE
    a.user_id = #{userId}
AND a.id = #{addrId}
AND a.deleted = 0

UPDATE app_express_address a
SET a.addr_default = 1
WHERE
    a.user_id = #{userId}
AND a.id = #{addrId}

UPDATE app_express_address a
SET a.addr_default = 0
WHERE
    a.user_id = #{userId}
AND a.addr_default=1
AND a.id <> #{addrId}
